import React, { useState } from "react";
import classes from './InfoInput.module.css'

const InfoInput = (props) => {

    const [hiddenPassword, setHiddenPassword] = useState(true)
    let isPass = false;

    if(props.name === 'password'){
        isPass = true
    }
    else {
        isPass = false
    }

    return(
        <div>
            {isPass ? (
                    <span>
                        <input type="password" {...props} className={classes.input} />
                        {hiddenPassword ? (
                            <span className={classes.open}><ion-icon name="eye-outline"></ion-icon></span>
                        ):(
                            <span className={classes.close}><ion-icon name="eye-off-outline"></ion-icon></span> 
                        )}
                    </span>
            ) : (
                <input type="text" {...props} className={classes.input}/>
            )}
        </div>
    )

}

export default InfoInput;
