import React, { useState } from "react";
import MainLogo from "../../MainLogo/MainLogo";
import InfoInput from "../InfoInput/InfoInput";
import FormButton from "../FormButton/FormButton";
import classes from './AuthForm.module.css'


import axios from "axios";

const AuthForm = ({setActive, setMotActive, ...props}) => {

    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
    
        try {
          const response = await axios.post('/api/login', { login, password });
          // Обработка успешной регистрации
          console.log(response.data);
        } catch (error) {
          // Обработка ошибки регистрации
          console.error(error);
        }
      };



    return(
        <section className={classes.content} onClick={ e => e.stopPropagation()}>
            {/* <MainLogo /> */}
            <h3 className={classes.form_title}>Авторизация</h3>
            <form onSubmit={() => handleSubmit()} className={classes.form}>
                <InfoInput onChange={e => setLogin(e.target.value)} placeholder="Логин/Имя"/>
                <InfoInput onChange={e => setPassword(e.target.value)} placeholder="Пароль"/>
                <FormButton name="Авторизоваться" />
            </form>
            <div className={classes.footer}>
              <span className="registr_link" onClick={() => {setActive(true); setMotActive(false)}}>Еще нету аккаунта. <span className={classes.registr}>Зарегистрироваться.</span></span>
            </div>
        </section>
    )

}

export default AuthForm;
