import React from "react";

const ProgLangs = () => {

    return(
        <div>
            <h2>Выберите языки:</h2>
            {options.map(option => (
                <div key={option.id}>
                <input
                    type="checkbox"
                    value={option.id}
                    checked={selectedValues.includes(option.id)}
                    onChange={handleCheckboxChange}
                />
                <label>{option.name}</label>
                </div>
            ))}
            <p>Выбранные языки: {selectedValues.join(', ')}</p>
        </div>
    )

}

export default ProgLangs;
