import './App.css';
import axios from 'axios'
import React, { useEffect, useState } from 'react'
import Header from './components/page-sections/header/Header';
// import headerMenuData from './data/header-menu.json'

function App() {

    const [repos, setRepos] = useState([]);
    const [headerMenuData, setHeaderMenuData] = useState({});

    useEffect(() => {
        let data;
        axios.get('http://127.0.0.1:8000/repositories/')
        .then( res => {
            data = res.data;
            setRepos(data);
        })
        .catch(err => {
            console.log(err)
        })

        setHeaderMenuData(getFile('./data/header-menu.json'))

    }, [])

    function getFile (fileName) {

        let data;
        var request = new XMLHttpRequest();
        request.open('GET', fileName);
        request.onloadend = function() {
            data = parse(request.responseText);
        }
        request.send();
        return data;
    }

    function parse(obj) {
        let data = JSON.parse(obj);
        return data
    }

    return(
        <div>
            <Header headerMenuData={headerMenuData} />
            {repos.map((output, id) => (
                <div className="repo-container" key={id}>
                    <div className="repo-header">
                        <div className="icon"></div>
                        <h3 className="repo-title">{output.full_name}</h3>
                    </div>
                    <div className="repo-description">
                        <p className="content">
                            {output.description}
                        </p>
                    </div>
                    <div className="repo-footer">
                        <span className="code-lang"><span className="circle"></span>{output.programmingLanguage.name}</span>
                        <span className="stargazers">{output.stargazers}</span>
                        <span className="forks">{output.forks}</span>
                        <span className="contributors"><a href="#" className="contributors-link">Contributors</a></span>
                    </div>
                </div>
            ))}
        </div>
    )

}

export default App;
