import React, { useState } from "react";
import axios from "axios";
import Cookies from 'js-cookie';

import classes from './AuthForm.module.css'

import InfoInput from "../InfoInput/InfoInput";
import FormButton from "../FormButton/FormButton";
import ProgLangs from "../pop_up/ProgLangs";

const RegForm = ({setActive, setNotActive, programmingLang}) => {

    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');
    const [mail, setMail] = useState('');
    const [selectedValues, setSelectedValues] = useState([]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        const UserData = {
            login: "login",
            password: "password",
            email: "qwerty@mail.ru",
            loveProgramming: ["Python", 'C++', "Rust"],
            githubUser: null
        }
    
        try {
          const response = await axios.post('http://127.0.0.1:8000/register/', UserData);
          // Обработка успешной регистрации
          console.log(response.data);
        } catch (error) {
          // Обработка ошибки регистрации
          console.error(error);
        }
      };



    return(
        <section className={classes.content} onClick={ e => e.stopPropagation()}>
            {/* <MainLogo /> */}
            <h3 className={classes.form_title}>Регистрация</h3>
            <form onSubmit={handleSubmit} className={classes.form}>
                <InfoInput value={login} onChange={e => setLogin(e.target.value)} placeholder="Логин/Имя"/>
                <InfoInput value={mail} onChange={e => setMail(e.target.value)} placeholder="Почта"/>
                <InfoInput value={password} onChange={e => setPassword(e.target.value)} placeholder="Пароль"/>
                <ProgLangs selectedValues={selectedValues} setSelectedValues={setSelectedValues} />
                <FormButton name="Зарегистрироваться" />
            </form>
            <div className={classes.footer}>
                <span className={classes.link} onClick={() => {setActive(true); setNotActive(false)}}>Уже есть аккаунт. 
                    <span className={classes.registr}> Авторизоваться.</span>
                </span>
            </div>
        </section>
    )

}

export default RegForm;
