import React, { useState } from "react";
import MainLogo from "../../MainLogo/MainLogo";
import InfoInput from "../InfoInput/InfoInput";
import FormButton from "../FormButton/FormButton";
import classes from './AuthForm.module.css'

const AuthForm = () => {

    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');



    return(
        <section className={classes.content}>
            <MainLogo />
            <h3 className="form_title">Авторизация</h3>
            <form action="post">
                <InfoInput setValue={setLogin} />
                <InfoInput setValue={setPassword} />
                <FormButton name="Авторизоваться" />
            </form>
            <div className="footer"><a href="/registr" className="registr_link">Еще нету аккаунта. Зарегистрироваться.</a></div>
        </section>
    )

}

export default AuthForm;
