import React, { useState } from "react";
import classes from './MenuItem.module.css'

const MenuItem = (props) => {

    const [visible, setVisible] = useState(false)

    let liClass = '';

    if (props.type === 'header-menu'){
        liClass += ' ' + classes.header_menu_item
    }

    if (props.name === 'Главная' || props.isAuth){
        setVisible(true)
    }

    return(
        <li className={liClass}>
            {visible ? (
                <a href={props.source} className="nav-menu-link">{props.name}</a>
            ): (
                <span className="nav-menu-link" onClick={() => props.setStatus(true) } >{props.name}</span>
            )}    
        </li>
    )

}

export default MenuItem;
